import{_ as t,c as i,o as e,ag as l}from"./chunks/framework.lXlForvM.js";const h=JSON.parse('{"title":"Installation","description":"","frontmatter":{},"headers":[],"relativePath":"guide/installation.md","filePath":"guide/installation.md"}'),n={name:"guide/installation.md"};function o(s,a,r,d,u,p){return e(),i("div",null,[...a[0]||(a[0]=[l('<h1 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h1><h2 id="via-brat-recommended-for-beta-testing" tabindex="-1">via BRAT (Recommended for Beta Testing) <a class="header-anchor" href="#via-brat-recommended-for-beta-testing" aria-label="Permalink to &quot;via BRAT (Recommended for Beta Testing)&quot;">​</a></h2><p>YAML Manipulator is currently in beta. Install it using BRAT (Beta Reviewers Auto-update Tester):</p><h3 id="step-1-install-brat" tabindex="-1">Step 1: Install BRAT <a class="header-anchor" href="#step-1-install-brat" aria-label="Permalink to &quot;Step 1: Install BRAT&quot;">​</a></h3><ol><li>Open Obsidian Settings</li><li>Navigate to <strong>Community Plugins</strong></li><li>Disable <strong>Safe Mode</strong> (if enabled)</li><li>Click <strong>Browse</strong> and search for &quot;BRAT&quot;</li><li>Install and enable BRAT</li></ol><h3 id="step-2-add-yaml-manipulator" tabindex="-1">Step 2: Add YAML Manipulator <a class="header-anchor" href="#step-2-add-yaml-manipulator" aria-label="Permalink to &quot;Step 2: Add YAML Manipulator&quot;">​</a></h3><ol><li>Open BRAT settings (Settings → BRAT)</li><li>Click <strong>Add Beta Plugin</strong></li><li>Enter: <code>ramnathk/obsidian-yaml-manipulator</code></li><li>Click <strong>Add Plugin</strong></li></ol><h3 id="step-3-enable-plugin" tabindex="-1">Step 3: Enable Plugin <a class="header-anchor" href="#step-3-enable-plugin" aria-label="Permalink to &quot;Step 3: Enable Plugin&quot;">​</a></h3><ol><li>Go to Settings → Community Plugins</li><li>Find <strong>YAML Manipulator</strong> in the list</li><li>Toggle it <strong>on</strong></li></ol><h2 id="manual-installation" tabindex="-1">Manual Installation <a class="header-anchor" href="#manual-installation" aria-label="Permalink to &quot;Manual Installation&quot;">​</a></h2><p>For manual installation or development:</p><ol><li>Download the latest release from <a href="https://github.com/ramnathk/obsidian-yaml-manipulator/releases" target="_blank" rel="noreferrer">GitHub Releases</a></li><li>Extract the archive</li><li>Copy <code>main.js</code>, <code>manifest.json</code>, and <code>styles.css</code> to:<div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>&lt;Your Vault&gt;/.obsidian/plugins/yaml-manipulator/</span></span></code></pre></div></li><li>Reload Obsidian</li><li>Enable the plugin in Settings → Community Plugins</li></ol><h2 id="verification" tabindex="-1">Verification <a class="header-anchor" href="#verification" aria-label="Permalink to &quot;Verification&quot;">​</a></h2><p>After installation, verify it&#39;s working:</p><ol><li>Open Command Palette (Cmd/Ctrl + P)</li><li>Type &quot;YAML&quot;</li><li>You should see YAML Manipulator commands</li></ol><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li><a href="/obsidian-yaml-manipulator/guide/quick-start.html">Quick Start Guide</a></li><li><a href="/obsidian-yaml-manipulator/guide/getting-started.html">Learn the basics</a></li></ul>',17)])])}const m=t(n,[["render",o]]);export{h as __pageData,m as default};
